import React, { PureComponent } from 'react'
import FormApp from './Form'

class Form extends PureComponent {
    constructor(){
        super() 
        this.state = {
            value : ''
        }

        this.handleChange = (e) => {
            console.log(e.target.textArea.value)
        }


    }


    componentDidMount (){

    }

    componentDidUpdate (){      
        console.log(this.state.text)
    }


    render () {     
        return(
            <FormApp value={this.state.value} handleChange={this.handleChange} />
        )
    }
}



export default Form
