import React, { PureComponent } from 'react'
import FormApp from './Form'

class Form extends PureComponent {
    constructor(){
        super() 
        this.state = {
            value : 'kk'
        }

        this.anyText = () => this.setState({ text : "text setado"})   
    }

    handlechange (e) {
        console.log(e.target.textArea.value)
    }

    componentDidMount (){
        this.anyText()
    }

    componentDidUpdate (){      
        console.log(this.state.text)
    }


    render () {     
        return(
            <FormApp value={this.state.value} handlechange={handlechange} />
        )
    }
}



export default Form
